INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (569, '2. Близка к средней скорости потока.', 180, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (570, '3. Значительно больше средней скорости потока.', 180, false);
