INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (583, '1. Все вертикальные элементы дорожных сооружений.', 185, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (584, '2. Только вертикальные элементы дорожных сооружений, представляющие опасность для движущихся транспортных средств.', 185, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (585, '1. Легковому автомобилю и маломестному автобусу.', 186, true);
