INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (594, '3. По любой из указанных.', 188, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (595, '1. Должен уступить дорогу, поскольку он двигается по полосе разгона.', 189, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (596, '2. Имеет преимущество, поскольку он двигается по полосе разгона.', 189, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (597, '3. Имеет преимущество, поскольку он находится справа от грузового автомобиля.', 189, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (598, '1. Предельные ограничения скорости, установленные Правилами.', 190, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (599, '2. Максимальная конструктивная скорость, установленная технической характеристикой используемого транспортного средства.', 190, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (600, '3. Условия видимости.', 190, true);
