INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (602, '2. Можно, если обгон будет завершен не ближе чем за 100 м до переезда.', 191, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (603, '3. Нельзя.', 191, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (604, '1. Только на хорошо просматриваемом месте на обочине.', 192, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (605, '2. Только на предусмотренных для этого площадках или за пределами дороги.', 192, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (606, '3. В любом из перечисленных мест.', 192, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (607, '1. Сразу начать движение.', 193, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (608, '2. Начать движение, убедившись в отсутствии только пешеходов, завершающих переход проезжей части.', 193, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (609, '3. Начать движение, убедившись в отсутствии пешеходов и транспортных средств, завершающих движение после смены сигнала светофора.', 193, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (610, '1. Только трамваю.', 194, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (611, '2. Только грузовому автомобилю.', 194, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (612, '3. Обоим транспортным средствам.', 194, true);
