INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (618, '3. Запрещен.', 196, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (619, '1. Только отдельно от ближнего или дальнего света фар.', 197, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (620, '2. Только совместно с ближним или дальним светом фар.', 197, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (621, '3. Как отдельно, так и совместно с ближним или дальним светом фар.', 197, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (622, '1. Автомобили.', 198, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (623, '2. Автобусы.', 198, false);
