INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1171, '3. Уступить дорогу легковому автомобилю и мотоциклу.', 374, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1172, '1. Вы имеете право считать, что находитесь на главной дороге.', 375, false);
