INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1176, '2. Имеете преимущество, так как автобус начинает движение с выездом на левую полосу.', 376, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1514, '3. Разворот.', 485, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1179, '3. Задние противотуманные фонари.', 377, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1180, '1. При выявлении и пресечении правонарушения.', 378, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1181, '2. Немедленно после вынесения постановления о лишении права управления транспортными средствами.', 378, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1182, '3. После вступления постановления о лишении права управления транспортными средствами в законную силу.', 378, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1183, '1. Прекратить начатое торможение.', 379, true);
