INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1443, '2. Разрешено, если Вы проживаете рядом с этим местом.', 463, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1444, '3. Запрещено.', 463, false);
