INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1446, '2. Только Б.', 464, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1447, '3. А и Б.', 464, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1448, '4. Б и В.', 464, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1449, '1. Место, где начинается (заканчивается) жилая зона.', 465, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1450, '2. Искусственную неровность на проезжей части.', 465, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1451, '3. Место, где начинается (заканчивается) зона с ограничением стоянки.', 465, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1452, '1. Предупреждает о неисправности светофора.', 466, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1453, '2. Разрешает движение и информирует о наличии нерегулируемого перекрестка или пешеходного перехода.', 466, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1454, '3. Запрещает дальнейшее движение.', 466, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1455, '1. Поворачивает в зоне действия знака «Движение прямо».', 467, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1456, '2. Поворачивает, не включив указатели поворота.', 467, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1457, '3. Совершает обе перечисленные ошибки.', 467, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1458, '1. Должны.', 468, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1459, '2. Должны, если он намерен повернуть направо.', 468, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1460, '3. Не должны.', 468, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1461, '1. Разрешается.', 469, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1462, '2. Разрешается только по траектории А.', 469, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1463, '3. Разрешается только по траектории Б.', 469, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1464, '4. Запрещается.', 469, false);
