INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1469, '2. Разрешено, если скорость грузового автомобиля менее 30 км/ч.', 471, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1470, '3. Запрещено.', 471, true);
