INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1511, '3. Вправо и влево от перекрестка организовано одностороннее движение.', 484, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1512, '1. Обгон.', 485, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1515, '4. Разрешаются все перечисленные маневры.', 485, true);
