INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1517, '2. На трамваи, а также другие маршрутные транспортные средства, движущиеся по выделенной для них полосе.', 486, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1518, '3. На все маршрутные транспортные средства.', 486, false);
