INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1520, '2. Обязаны только при наличии на перекрестке других транспортных средств.', 487, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1521, '3. Не обязаны.', 487, false);
