INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1544, '2. Только легковому автомобилю.', 495, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1545, '3. Легковому автомобилю и автобусу.', 495, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1546, '4. Всем транспортным средствам.', 495, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1547, '1. Проехать железнодорожный переезд без остановки перед знаком.', 496, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1548, '2. Остановиться перед знаком и продолжить движение сразу же после проезда поезда.', 496, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1549, '3. Остановиться перед знаком и продолжить движение, убедившись в отсутствии приближающегося поезда.', 496, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1550, '1. Только звуковым сигналом.', 497, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1551, '2. Только кратковременным переключением фар с ближнего света на дальний.', 497, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1552, '3. Только совместной подачей звукового и светового сигналов.', 497, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1553, '4. Любым из перечисленных способов.', 497, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1554, '1. Не работает указатель уровня топлива.', 498, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1555, '2. Нарушена регулировка угла опережения зажигания.', 498, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1556, '3. Затруднен пуск двигателя.', 498, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1557, '4. Не работает звуковой сигнал.', 498, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1558, '1. Не зависит.', 499, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1559, '2. Увеличивается пропорционально скорости.', 499, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1560, '3. Увеличивается пропорционально квадрату скорости.', 499, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1561, '1. Наложить давящую повязку на место ранения.', 500, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1562, '2. Наложить жгут выше места ранения.', 500, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1563, '3. Наложить жгут ниже места ранения.', 500, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1564, '1. Считается перекрестком равнозначных дорог.', 501, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1565, '2. Считается перекрестком неравнозначных дорог.', 501, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1566, '3. Не считается перекрестком.', 501, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1567, '1. Только А.', 502, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1568, '2. Только Б.', 502, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1569, '3. Б и В.', 502, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1570, '4. Все.', 502, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1571, '1. Только для предупреждения об обгоне.', 503, false);
