INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1866, '3. Лишение права управления транспортными средствами на срок от 1 года до 1,5 лет или административный арест на срок до 15 суток.', 598, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1867, '1. Привести в действие стояночный тормоз.', 599, true);
