INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1872, '1. Подкатегории «А1».', 601, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1873, '2. Подкатегории «В1» с мотоциклетной посадкой или рулем мотоциклетного типа.', 601, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1874, '3. Категории «М».', 601, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1875, '4. Всеми перечисленными транспортными средствами.', 601, true);
