INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1886, '1. Разрешается только при выезде из дворов и других прилегающих территорий.', 605, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1887, '2. Разрешается только при обгоне.', 605, false);
