INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1903, '2. Только если установлены дорожные знаки, разрешающие движение со скоростью более 60 км/ч.', 610, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1904, '3. В обоих перечисленных случаях.', 610, false);
