INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1959, '2. Всем механическим транспортным средствам.', 628, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1960, '3. Любым транспортным средствам и пешеходам.', 628, true);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1961, '1. Разрешено.', 629, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1962, '2. Разрешено только в населенном пункте.', 629, false);
INSERT INTO public.pdd_answer (id, pdd_answer_name, pdd_ques_id, istrue) VALUES (1963, '3. Разрешено только при видимости дороги не менее 100 м.', 629, false);
